import React, { useState } from 'react';
import axios from 'axios';

const PredictRainfallPage: React.FC = () => {
  const [formData, setFormData] = useState({
    region: '',
    jan: '',
    feb: '',
    mar: '',
    apr: '',
    may: '',
    jun: ''
  });
  const [prediction, setPrediction] = useState<number | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setPrediction(null);

    try {
      const response = await axios.post('http://localhost:8001/predict', {
        region: formData.region,
        jan: parseFloat(formData.jan),
        feb: parseFloat(formData.feb),
        mar: parseFloat(formData.mar),
        apr: parseFloat(formData.apr),
        may: parseFloat(formData.may),
        jun: parseFloat(formData.jun)
      });

      setPrediction(response.data.predicted_rainfall);
    } catch (err) {
      setError('Error making prediction. Please try again.');
      console.error(err);
    }
  };

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold text-gray-900 mb-6">Predict July Rainfall</h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="region" className="block text-sm font-medium text-gray-700">
            Region
          </label>
          <input
            type="text"
            name="region"
            id="region"
            value={formData.region}
            onChange={handleChange}
            required
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
          />
        </div>

        <div className="grid grid-cols-2 gap-4">
          {['jan', 'feb', 'mar', 'apr', 'may', 'jun'].map((month) => (
            <div key={month}>
              <label htmlFor={month} className="block text-sm font-medium text-gray-700">
                {month.toUpperCase()} Rainfall
              </label>
              <input
                type="number"
                name={month}
                id={month}
                value={formData[month as keyof typeof formData]}
                onChange={handleChange}
                required
                step="0.01"
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
              />
            </div>
          ))}
        </div>

        <button
          type="submit"
          className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
        >
          Predict
        </button>
      </form>

      {error && (
        <div className="mt-4 p-4 bg-red-50 border-l-4 border-red-500">
          <p className="text-red-700">{error}</p>
        </div>
      )}

      {prediction !== null && (
        <div className="mt-4 p-4 bg-blue-50 border-l-4 border-blue-500">
          <p className="text-blue-700">
            Predicted July Rainfall: <span className="font-bold">{prediction.toFixed(2)} mm</span>
          </p>
        </div>
      )}
    </div>
  );
};

export default PredictRainfallPage;
